import React, { useEffect, useState } from "react";
import axios from "axios";
import Translation from "./MTR_Dict.js"

function MTR_trainInfo({ line, station }) {
  const [trainSchedule, setTrainSchedule] = useState({});
  var { mtrStnApi } = "";

  line = "TCL";
  station = "SUN";

  useEffect(() => {
    mtrStnApi = `https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=${line}&sta=${station}`;

    axios
      .get(mtrStnApi)
      .then((res) => setTrainSchedule(res.data.data))
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    setInterval(() => {
      mtrStnApi = `https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=${line}&sta=${station}`;

      axios
        .get(mtrStnApi)
        .then((res) => setTrainSchedule(res.data.data))
        .catch((error) => console.log(error));
    }, 10000);
  }, []);

  return (
    <div className="mtr_traininfo">
      <table>
        <tr>
          <th>DOWN</th>
        </tr>
        <tr>
          <th>Dest</th>
          <th>Plat</th>
          <th>TTNT</th>
        </tr>

        {trainSchedule[line + "-" + station]?.DOWN.map((train) => (
          <tr>
              <td>{Translation.Station.en[train.dest]}</td>
            <td>{train.plat}</td>
            <td>{train.ttnt}</td>
            <td>{train.time}</td>
          </tr>
        ))}
      </table>

      <table>
        <tr>
          <th>UP</th>
        </tr>
        <tr>
          <th>Dest</th>
          <th>Plat</th>
          <th>TTNT</th>
        </tr>

        {trainSchedule[line + "-" + station]?.UP.map((train) => (
          <tr>
            <td>{train.dest}</td>
            <td>{train.plat}</td>
            <td>{train.ttnt}</td>
            <td>{train.time}</td>
          </tr>
        ))}
      </table>
    </div>
  );
}

export default MTR_trainInfo;
