import React, { useEffect, useState } from "react";
import axios from "axios";
import Translation from "./MTR_Dict.js";

function MTR_trainInfo({ line, station }) {
  const [trainSchUp, setTrainSchUp] = useState(null);
  const [trainSchDown, setTrainSchDown] = useState(null);

  line = "TCL";
  station = "SUN";

  const mtrStnApi = `https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=${line}&sta=${station}`;

  useEffect(() => {
    if (line != null && station != null) {
      setInterval(() => {
        axios
          .get(mtrStnApi)
          .then((res) => setTrainSchUp(res.data.data[line + "-" + station].DOWN))
          .then((res) => setTrainSchDown(res.data.data[line + "-" + station].UP))
          .catch((error) => console.log(error));
      }, 10000);
    }
  }, []);

  return (
    <div className="mtr_traininfo">
      <p>Station: {Translation.Station.en[station]}</p>

      <table>
        <tr>
          <th>{Translation.Line.en[line]} DOWN</th>
        </tr>

        {trainSchDown.map((train) => (
          <tr>
            <td>{Translation.Station.en[train.dest]}</td>
            <td className="train__plat">{train.plat}</td>
            <td>{train.ttnt < 1 ? "Arriving" : train.ttnt + " min"}</td>
          </tr>
        ))}
      </table>
      <p />
      <table>
        <tr>
          <th>{Translation.Line.en[line]} UP</th>
        </tr>

        {trainSchedule[line + "-" + station]?.UP.map((train) => (
          <tr>
            <td>{Translation.Station.en[train.dest]}</td>
            <td className="train__plat">{train.plat}</td>
            <td>{train.ttnt < 1 ? "Arriving" : train.ttnt + " min"} </td>
          </tr>
        ))}
      </table>
    </div>
  );
}

export default MTR_trainInfo;
