import React, { useEffect, useState } from "react";
import axios from "axios";

function MTR_trainInfo({ line, station }) {
  const [trainSchedule, setTrainSchedule] = useState({});
  var { mtrStnApi } = "";

  line = "TCL";
  station = "SUN";

  useEffect(() => {
    mtrStnApi = `https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=${line}&sta=${station}`;

    axios
      .get(mtrStnApi)
      .then((res) => setTrainSchedule(res.data.data))
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    setInterval(() => {
      mtrStnApi = `https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=${line}&sta=${station}`;

      axios
        .get(mtrStnApi)
        .then((res) => setTrainSchedule(res.data.data))
        .catch((error) => console.log(error));
    }, 10000);
  }, []);

  return (
    <div className="mtr_traininfo">
      <table>
        <tr>
          <th>Dest</th>
          <th>Plat</th>
          <th>TTNT</th>
        </tr>

        {trainSchedule[line + "-" + station]?.DOWN.map((train) => (
          <tr>
            <td>{train.dest}</td>
            <td>{train.plat}</td>
            <td>{train.ttnt}</td>
            <td>{new Date(train.time).getMilliseconds()}</td>
          </tr>
        ))}

        {console.log(trainSchedule[line + "-" + station]?.DOWN)}
      </table>
    </div>
  );
}

export default MTR_trainInfo;
